import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ENDPOINTS } from '../endpoints';
import { AuthenticationService } from '../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-update',
  templateUrl: './update.component.html',
  styleUrls: ['./update.component.scss'],
})
export class UpdateComponent implements OnInit {
  token: any = undefined;
  constructor(
    private readonly http: HttpClient,
    private readonly authService: AuthenticationService,
    private readonly router: Router
  ) {}
  ngOnInit(): void {
    // check if parsing is required then use JSON.parse()
    this.token = this.authService.currentUserValue;
  }

  onUpdate() {
    this.http.post(ENDPOINTS.update, {}).subscribe({
      next: (res) => {
        this.router.navigate(['/info']);
      },
      error: (err) => {
        alert('something went wrong');
      },
    });
  }
}
<div class="w-1/2 mx-auto text-center">
  <h1 class="text-xl">Update Token</h1>
  <div class="flex">
    <div class="bg-gray-200 w-1/2 b-1 p-4 border-solid border border-black">
      {{ "Token" }}
    </div>
    <div
      class="bg-gray-300 w-1/2 b-1 p-4 border-solid border border-black break-words"
    >
      <span> {{ token.token }}</span>
    </div>
  </div>

  <div class="flex">
    <div class="bg-gray-200 w-1/2 b-1 p-4 border-solid border border-black">
      {{ "User Name" }}
    </div>
    <div class="bg-gray-300 w-1/2 b-1 p-4 border-solid border border-black">
      {{ token.username }}
    </div>
  </div>

  <button
    class="my-2 py-2 bg-blue-300 text-white w-1/2 mx-2"
    (click)="onUpdate()"
  >
    update
  </button>
</div>
