import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ENDPOINTS } from '../endpoints';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent {
  loginForm!: FormGroup;
  constructor(
    private readonly fb: FormBuilder,

    private readonly http: HttpClient,
    private readonly router: Router
  ) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
    });
  }

  onLogin() {
    if (this.loginForm.invalid) {
      return;
    }
    this.http.post(ENDPOINTS.login, this.loginForm.value).subscribe({
      next: (res) => {
        // put token in local storage
        localStorage.setItem('token', JSON.stringify(res));
        console.log('ok');
        this.router.navigate(['/info']);
      },
      error: (err) => {
        alert('something went wrong');
      },
    });
    console.log(this.loginForm.value);
  }

  onRegister() {
    if (this.loginForm.invalid) {
      return;
    }
    this.http.post(ENDPOINTS.register, this.loginForm.value).subscribe({
      next: (res) => {
        // put token in local storage
        localStorage.setItem('token', JSON.stringify(res));
        this.router.navigate(['/info']);
        console.log('ok');
      },
      error: (err) => {
        alert('something went wrong');
      },
    });
    console.log(this.loginForm.value);
  }
}
<div class="w-2/6 mx-auto">
  <h1 class="text-center">Login</h1>

  <form [formGroup]="loginForm">
    <!-- email input field -->
    <div class="my-2">
      <label for="email" class="block mb-2 text-sm font-medium text-gray-900"
        >Email</label
      >
      <input
        formControlName="email"
        type="text"
        id="email"
        class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
        placeholder="name"
      />
      <div
        *ngIf="
          loginForm.controls['email'].touched &&
          loginForm.controls['email'].errors
        "
        id="email_error"
        class="text-red-500"
      >
        Please enter a valid email
      </div>
    </div>
    <!-- password -->
    <div class="my-2">
      <label for="password" class="block mb-2 text-sm font-medium text-gray-900"
        >Password</label
      >
      <input
        formControlName="password"
        type="password"
        id="password"
        class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
        placeholder="password"
      />
    </div>
    <!-- login button -->
    <div class="flex">
      <button
        class="my-2 py-2 bg-blue-500 text-white w-1/2 mx-2"
        (click)="onRegister()"
      >
        Register
      </button>

      <button
        class="my-2 py-2 bg-blue-300 text-white w-1/2 mx-2"
        (click)="onLogin()"
      >
        Login
      </button>
    </div>
  </form>
</div>
